    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{ 
             Class.forName("com.mysql.jdbc.Driver");
              Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/sorubanka_database?useTimezone=true&serverTimezone=UTC","root","01A06m0550alp.");
         String insert="INSERT INTO ogretmen_uyari_tablo(uyari_soru,uyari_ogretmen) values('"+txt_hata.getText()+"','"+txt_ogretmen.getText()+"')";
        PreparedStatement pst=con.prepareStatement(insert);
       
         
         pst.executeUpdate();
         JOptionPane.showMessageDialog(null, "BAŞARILI");
       
        }
        catch(Exception ex)
        {
             JOptionPane.showMessageDialog(null, ex);
        }
        sil();
        listele();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
            DefaultTableModel model=(DefaultTableModel)jTable1.getModel();
        int selectedRowIndex=jTable1.getSelectedRow();
        txt_hata.setText(model.getValueAt(selectedRowIndex,1).toString());
        txt_ogretmen.setText(model.getValueAt(selectedRowIndex,2).toString());
        
    }//GEN-LAST:event_jTable1MouseClicked

