private void listele()
{
    
        try{
            Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/sorubanka_database?useTimezone=true&serverTimezone=UTC","root","01A06m0550alp.");
           
            PreparedStatement pst=con.prepareStatement("SELECT * from uyari_tablo");
           ResultSet rs= pst.executeQuery();
           
           
            DefaultTableModel df=(DefaultTableModel)jTable1.getModel();
            df.setRowCount(0);
            while(rs.next())
            {
                
             Object o[]={rs.getInt("id_uyari"),rs.getString("yanlis_soru"),rs.getString("ogretmen")};
             df.addRow(o);
            }
       }catch(Exception e)
       {
             JOptionPane.showMessageDialog(null, e);
       }
}
private void sil()
{
 DefaultTableModel model=(DefaultTableModel)jTable1.getModel();
        int indix=jTable1.getSelectedRow();
        int id=Integer.parseInt(model.getValueAt(indix, 0).toString());
         try{ 
             Class.forName("com.mysql.jdbc.Driver");
             Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/sorubanka_database?useTimezone=true&serverTimezone=UTC","root","01A06m0550alp.");
       
                
            PreparedStatement pr;
         
            pr =con.prepareStatement("delete from uyari_tablo where yanlis_soru='"+txt_hata.getText()+"'");
             
         //   pr.setInt(1, id);
      
         pr.executeUpdate();
         JOptionPane.showMessageDialog(null, "BAŞARILI");
        
        }
        catch(Exception ex)
        {
             JOptionPane.showMessageDialog(null, ex);
        }
}


